    PRODUCTOS_ID_PRODUCTO_FK NUMBER NOT NULL,
    ESTADOS_ID_ESTADO_FK NUMBER NOT NULL,
    CANTIDAD NUMBER,
    PRECIO_UNITARIO NUMBER(10,2),
